const displayText=document.getElementById('disrupt'),inputText=document.getElementById('disrupt-edit'),playForm=document.getElementById('play-form'),editForm=document.getElementById('edit-form'),effectsSelect=document.getElementById('effects');function doDisrupt(){displayText.className=`disrupt ${effectsSelect.value}`,window.DISRUPT.forceStopDisruptions(),window.DISRUPT.addDisruptions()}inputText.addEventListener('keydown',a=>{13===a.keyCode&&finishEdit()});function startEdit(){window.DISRUPT.forceStopDisruptions(),displayText.classList.add('hidden'),inputText.classList.remove('hidden'),playForm.classList.add('hidden'),editForm.classList.remove('hidden'),inputText.focus(),inputText.selectionStart=inputText.selectionEnd=inputText.value.length}function finishEdit(){if(0<inputText.value.length){let a=document.createTextNode(inputText.value);displayText.innerHTML='',displayText.appendChild(a),displayText.classList.remove('hidden'),inputText.classList.add('hidden'),playForm.classList.remove('hidden'),editForm.classList.add('hidden')}else alert('Please enter some text!')}
